import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Fade from '@mui/material/Fade';
import Typography from '@mui/material/Typography';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

import React, { useState } from 'react';

const produceDisplayText = (shift) => {

    const fields = ["name", "start", "end", "qual_required", "nurse_id"];

    fields.map((field) => {
        // Process datetime (i.e. start & end)
        if (timeFields.includes(field)) {
            return parseDateTime(shift[field]);
        }

        // Process nurse ID
        else if (field === 'nurse_id') {
            const nurseId = shift[field];
            if (!nurseId) {
                return '';
            }

            const nurse = nursesData.find(nur => nur.id === nurseId);
            const nurseString = nurse.first_name
                + " "
                + nurse.last_name
                + ", "
                + nurse.qualification;

            return nurseString;
        }

        // All other fields
        return shift[field];

    });

}


const ShiftDropdown = ({ shiftsData }) => {
    const [shift, setShift] = useState('a');
    const handleChange = (val) => {
        setShift(val);
    }

    return (
        <Box sx={{ minWidth: 120 }}>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Shift</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={shift}
                    label="Age"
                    onChange={handleChange}
                >
                    {shiftsData.map((shift) => {

                    })}
                    <MenuItem value={10}>Ten</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                </Select>
            </FormControl>
        </Box>

    )
}

export const SetShiftModal = ({ isOpen, handleClose }) => {
    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 400,
        bgcolor: 'background.paper',
        border: '2px solid #000',
        boxShadow: 24,
        p: 4,
    };

    return (<>
        <Modal
            aria-labelledby="transition-modal-title"
            aria-describedby="transition-modal-description"
            open={isOpen}
            onClose={handleClose}
            closeAfterTransition
            BackdropComponent={Backdrop}
            BackdropProps={{
                timeout: 500,
            }}
        >
            <Fade in={isOpen}>
                <Box sx={style}>
                    <Typography id="transition-modal-title" variant="h6" component="h2">
                        Set Shift Assignment
                    </Typography>
                    <ShiftDropdown />
                    <Typography id="transition-modal-description" sx={{ mt: 2 }}>
                        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                    </Typography>
                </Box>
            </Fade>
        </Modal>
    </>);
}
