import React, { useState, useEffect } from 'react';

import { Button } from '@mui/material';

import './App.css';

import { ShiftsTable } from './ShiftsTable';
import { SetShiftModal } from './SetShiftModal';


function App() {
  const [nursesList, setNursesList] = useState([]);
  const [shiftsList, setShiftsList] = useState([]);
  const [justSaved, setJustSaved] = React.useState(false);

  const callApi3_2 = async () => {
    var responseClone;
    const response = await fetch('http://localhost:9001/nurses', {
      method: "GET",
    }).then(function (response) {
      responseClone = response.clone();
      return responseClone.text()
        .then(function (bodyText11) {
          console.log({ bodyText11 });
          return { bodyText11 };
        });
    });
  };

  const fetchNurses = async () => {
    const response = await fetch('http://localhost:9001/nurses');
    const respText = await response.text();
    if (response.status !== 200) throw Error(respText.message);

    return respText;
  };

  const fetchShifts = async () => {
    const response = await fetch('http://localhost:9001/shifts');
    const respText = await response.text();
    if (response.status !== 200) throw Error(respText.message);

    console.log("SHIFTS FETCHED");
    console.log(respText);

    return respText;
  };

  const saveShiftAssignment = async (shiftId, nurseID) => {
    console.log("In saveShiftAssignment");
    console.log({ shiftId, nurseID });

    const requestOptions = {
      method: 'PUT',
      headers: { 'Content-Type': 'text/plain' },
      body: nurseID
    };

    fetch('http://localhost:9001/shifts/' + shiftId + '/' + nurseID, requestOptions)
      .then(res => {
        if (res.status !== 200) {
          console.log("error")
        } else {
          res.json().then(data => {
            console.log(data);
            fetchShifts().then(() => {
              console.log("DONE SAVING!!!")
            });
          })
        }
      });


  };

  useEffect(() => {
    console.log("UPDATING TABLE DATA");
    setJustSaved(false);
    fetchNurses()
      .then(res => {
        // console.log({ res });
        // console.log(JSON.parse(res));

        // Need to parse because response is a string literal containing an array of objects
        const nurses = JSON.parse(res);
        setNursesList(nurses);

        console.log("End of fetchNurses in useEffect -- Success");
      }).catch(err => {
        console.log(err);
        console.log("End of fetchNurses in useEffect -- Failure");
      });

    fetchShifts()
      .then(res => {
        // console.log({ res });
        // console.log(JSON.parse(res));

        // Need to parse because response is a string literal containing an array of objects
        const shifts = JSON.parse(res);
        setShiftsList(shifts);

        console.log("End of fetchShifts in useEffect -- Success");
      }).catch(err => {
        console.log(err);
        console.log("End of fetchShifts in useEffect -- Failure");
      });

  }, [justSaved])

  useEffect(() => {
    console.log("Start of useEffect");
    fetchNurses()
      .then(res => {
        // console.log({ res });
        // console.log(JSON.parse(res));

        // Need to parse because response is a string literal containing an array of objects
        const nurses = JSON.parse(res);
        setNursesList(nurses);

        console.log("End of fetchNurses in useEffect -- Success");
      }).catch(err => {
        console.log(err);
        console.log("End of fetchNurses in useEffect -- Failure");
      });

    fetchShifts()
      .then(res => {
        // console.log({ res });
        // console.log(JSON.parse(res));

        // Need to parse because response is a string literal containing an array of objects
        const shifts = JSON.parse(res);
        setShiftsList(shifts);

        console.log("End of fetchShifts in useEffect -- Success");
      }).catch(err => {
        console.log(err);
        console.log("End of fetchShifts in useEffect -- Failure");
      });
  }, []);

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <div className="App">
      <h1> Shift Tracker </h1>

      <div className="set-shift-asgnmt-btn">
        <Button onClick={handleOpen} variant="contained">Set Shift Assignment</Button>
      </div>

      <SetShiftModal isOpen={open}
        handleOpen={handleOpen}
        handleClose={handleClose}
        shiftsData={shiftsList}
        nursesData={nursesList}
        saveShiftAssignment={saveShiftAssignment}
        setJustSaved={setJustSaved}
      />

      <div className="shift-table">
        <ShiftsTable shiftsData={shiftsList} nursesData={nursesList} />
      </div>

    </div>
  );
}

export default App;
