import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Fade from '@mui/material/Fade';
import Typography from '@mui/material/Typography';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

import React, { useState } from 'react';

const produceDisplayText = (shift) => {

    console.log("In produceDisplayText");

    const fields = ["name", "start", "end", "qual_required", "nurse_id"];
    const neededFields = ["name", "start", "qual_required"];

    const shiftStr = shift.name + " " + parseDateTime(shift.start) + " " + shift.qual_required;
    console.log(shiftStr);

    return shiftStr;
}

const parseDateTime = (iso) => {
    const removeFrontZero = (str) => {
        if (str.length === 2 && str[0] === '0') {
            return str[1];
        }
    }

    // Break up date and time 2021-08-01T09:00:00Z
    const date_time_split = iso.split("T");
    var [date, time] = [date_time_split[0], date_time_split[1]];

    // Convert YYYY-MM-DD -> MM/DD/YY
    const date_split = date.split("-");
    var [year, month, day] = [date_split[0],
    date_split[1],
    date_split[2]];

    const dateString = removeFrontZero(month)
        + '/'
        + removeFrontZero(day)
        + '/'
        + year;

    // Convert time 13:00:00Z -> 1:00:00 AM
    const time_split = time.slice(0, -1).split(':');
    const [hour, min, sec] = [time_split[0],
    time_split[1],
    time_split[2]];

    const period = (hour < 12)
        ? 'AM'
        : 'PM';

    var newHour = parseInt(hour) > 12
        ? parseInt(hour) - 12
        : (parseInt(hour) === 0
            ? 12
            : parseInt(hour));

    newHour = newHour.toString();

    const timeString = [newHour, min, sec].join(":")
        + " "
        + period;

    const dateTimeDisplay = dateString + " " + timeString;

    return dateTimeDisplay;

    // console.log({ dateTimeDisplay, hour, newHour, min, sec });
};


const ShiftDropdown = ({ shiftsData }) => {
    const [shift, setShift] = useState('a');
    const handleChange = (val) => {
        setShift(val);
    }

    console.log("In ShiftDropdown");
    console.log({shiftsData});

    return (
        <Box sx={{ minWidth: 120 }}>
            <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">Shift</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={shift}
                    label="Age"
                    onChange={handleChange}
                >
                    {shiftsData.map((shift) => {
                        <MenuItem value={shift.id}>{shift.name + " " + parseDateTime(shift.start) + " " + shift.qual_required}</MenuItem>
                    })}
                    <MenuItem value={10}>Ten</MenuItem>
                    <MenuItem value={20}>Twenty</MenuItem>
                    <MenuItem value={30}>Thirty</MenuItem>
                </Select>
            </FormControl>
        </Box>

    )
}

export const SetShiftModal = ({ isOpen, handleClose, shiftsData }) => {
    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 400,
        bgcolor: 'background.paper',
        border: '2px solid #000',
        boxShadow: 24,
        p: 4,
    };

    return (<>
        <Modal
            aria-labelledby="transition-modal-title"
            aria-describedby="transition-modal-description"
            open={isOpen}
            onClose={handleClose}
            closeAfterTransition
            BackdropComponent={Backdrop}
            BackdropProps={{
                timeout: 500,
            }}
        >
            <Fade in={isOpen}>
                <Box sx={style}>
                    <Typography id="transition-modal-title" variant="h6" component="h2">
                        Set Shift Assignment
                    </Typography>
                    <ShiftDropdown shiftsData={shiftsData} />
                    <Typography id="transition-modal-description" sx={{ mt: 2 }}>
                        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                    </Typography>
                </Box>
            </Fade>
        </Modal>
    </>);
}
