import { Paper, TableContainer, Table, TableHead, TableBody, TableRow, TableCell } from '@mui/material';

export const ShiftsTable = ({ shiftsData, nursesData }) => {
    const data = [
        { name: "John", email: "john@gmail.com", age: 12, gender: "Male" },
        { name: "Bren", email: "bren@gmail.com", age: 24, gender: "Male" },
        { name: "Marry", email: "marry@gmail.com", age: 18, gender: "Female" },
        { name: "Shohail", email: "shohail@gmail.com", age: 25, gender: "Male" },
        { name: "Aseka", email: "aseka@gmail.com", age: 19, gender: "Female" },
        { name: "Meuko", email: "meuko@gmail.com", age: 12, gender: "Female" },
    ];

    const fields = ["name", "start", "end", "qual_required", "nurse_id"];
    const timeFields = ["start", "end"];

    return (
        <TableContainer
            component={Paper}
            variant="outlined"
        >
            <Table sx={{ maxWidth: '100vw' }} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        {fields.map((field) => (
                            <TableCell key={field}>{field}</TableCell>
                        ))}
                    </TableRow>
                </TableHead>
                <TableBody>
                    {shiftsData.map((shift) => (
                        <TableRow>
                            {fields.map((field) => {
                                const parseDateTime = (iso) => {
                                    const removeFrontZero = (str) => {
                                        if (str.length === 2 && str[0] === '0') {
                                            return str[1];
                                        }
                                    }

                                    // Break up date and time 2021-08-01T09:00:00Z
                                    const date_time_split = iso.split("T");
                                    var [date, time] = [date_time_split[0], date_time_split[1]];

                                    // Convert YYYY-MM-DD -> MM/DD/YY
                                    const date_split = date.split("-");
                                    var [year, month, day] = [date_split[0],
                                    date_split[1],
                                    date_split[2]];

                                    console.log({ year, month, day, time });

                                    const dateString = removeFrontZero(month)
                                        + '/'
                                        + removeFrontZero(day)
                                        + '/'
                                        + year;

                                    console.log({ dateString });

                                    // Convert time 13:00:00Z -> 1:00:00 AM
                                    const time_split = time.slice(0, -1).split(':');
                                    const [hour, min, sec] = [time_split[0],
                                    time_split[1],
                                    time_split[2]];

                                    const period = (hour < 12)
                                        ? 'AM'
                                        : 'PM';

                                    var newHour = parseInt(hour) > 12
                                        ? parseInt(hour) - 12
                                        : (parseInt(hour) === 0
                                            ? 12
                                            : parseInt(hour));

                                    newHour = newHour.toString();

                                    const timeString = [newHour, min, sec].join(":")
                                        + " "
                                        + period;

                                    console.log("--- FINAL DATE + TIME TO DISPLAY ---")
                                    console.log(dateString + " " + timeString);

                                    const dateTimeDisplay = dateString + " " + timeString;

                                    return dateTimeDisplay;

                                    // console.log({ hour, newHour, min, sec });
                                };

                                const displayText = timeFields.includes(field)
                                    ? parseDateTime(shift[field])
                                    : shift[field];
                                return <TableCell key={field + shift.id}>{displayText}</TableCell>;
                            })}
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>

    );

};
